package javaProgram;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 22;
	    if (a < 10) {
	      System.out.println("Dog");
	    } else if (a < 18) {
	      System.out.println("Cat");
	    }  else {
	      System.out.println("Tiger");
	    }
		
	
	}

}
