package javaProgram;

public class LoopInnerRoute {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//outer loop
				for(int i=0; i<=2; i++) {
		        System.out.println("outer: "+i);

		    //inner loop
		        for(int j=1; j<=3; j++ ) {
		        System.out.println("Inner: "+j);        	
		        }
			}
			

	}

}
